// Placeholder stuff
$("[data-placeholder]").each(function(){if($.trim($(this).data("placeholder"))=="")return!0;$(this).on({focus:function(){var e=$(this);if(e.val()==e.data("placeholder")){e.val("");e.removeClass("placeholder")}},blur:function(){var e=$(this);if(e.val()==""||e.val()==e.data("placeholder")){e.val(e.data("placeholder"));e.addClass("placeholder")}}}).trigger("blur");$("#signup-form").submit(function(e){var t=$(this);t.find("[data-placeholder]").each(function(){var e=$(this);e.val()==e.data("placeholder")&&e.val("")});$.getJSON(this.action+"?callback=?",t.serialize(),function(e){if(e.Status===400)t.find("input.email").addClass("error");else{t.find("p").hide();t.append('<p class="message">Weâ€™ve got you. Thanks for your interest.</p>')}});return!1})});